using System; using System.Collections.Generic; using System.Linq; using System.Reflection; using System.Text;  namespace EasyReflection.Cloning {     public class ListClonerProvider : IClonerProvider     {         private readonly List<IObjectCloner> cloners = new List<IObjectCloner>();          public ListClonerProvider AddCloner(IObjectCloner Cloner)         {             cloners.Add(Cloner);             return this;         }          public IObjectCloner GetCloner(PropertyInfo PropertyInfo)         {             foreach (var cln in cloners)             {                 if (cln.IsClonable(PropertyInfo))                 {                     return cln;                 }             }             return null;         }          public IObjectCloner GetCloner(Type Type)         {             foreach (var cln in cloners)             {                 if (cln.IsClonable(Type))                 {                     return cln;                 }             }             return null;         }     } } 